mapper.insert:
insert into store
id,name,src,location,ownerId,ownerName,income,registerTime,info
#{id, jdbcType=VARCHAR}, #{name, jdbcType=VARCHAR}, #{src, jdbcType=VARCHAR}, #{location, jdbcType=VARCHAR}, #{ownerId, jdbcType=VARCHAR}, #{ownerName, jdbcType=VARCHAR}, #{income, jdbcType=FLOAT}, #{registerTime, jdbcType=VARCHAR}, #{info, jdbcType=VARCHAR}

insert into dish
id,src,name,price,sellCount,storeId
#{id, jdbcType=VARCHAR}, #{src, jdbcType=VARCHAR}, #{name, jdbcType=VARCHAR}, #{price, jdbcType=FLOAT}, #{sellCount, jdbcType=INTEGER}, #{storeId, jdbcType=VARCHAR}

mapper.update:
update dish

	name=#{name, jdbcType=VARCHAR},
	src=#{src,jdbcType=VARCHAR},
	price=#{price, jdbcType=FLOAT},
	sellCount=#{sellCount, jdbcType=INTEGER}

where id=#{id,jdbcType=VARCHAR}

update store
src=#{src, jdbcType=VARCHAR}, name=#{name, jdbcType=VARCHAR}, info=#{info, jdbcType=VARCHAR}, location=#{location, jdbcType=VARCHAR}, lng=#{lng,jdbcType=DECIMAL}, lat=#{lat,jdbcType=DECIMAL}
where id=#{id,jdbcType=VARCHAR}
